var app=app||{};app.TableFooterView=Backbone.View.extend({el:"#table-footer",events:{"click #reset-button":"resetList"},initialize:function(){this.$totalCalories=$(".calorie-total"),this.$fatTotal=$(".fat-total"),this.listenTo(app.Foods,"all",this.total)},resetList:function(){app.Foods.reset(),localStorage.clear()},total:function(){for(var a=app.Foods.length,b=0,c=0,d=0;d<a;d++)b+=app.Foods.models[d].get("calories"),c+=app.Foods.models[d].get("total_fat");this.$fatTotal.text(c.toFixed(2)+" (g)"),this.$totalCalories.text(b.toFixed(2)+" (KCal)")}});